            return false;
        }


        if (!ecAff_isOnCurve(Q[0], Q[1])) {
            return false;
        }

        uint sInv = FCL_nModInv(n-rs[1]);

        uint scalar_u=mulmod(uint(message), sInv, n);
        uint scalar_v= mulmod(rs[0], sInv, n);
        uint x1;

       x1=ecZZ_mulmuladd_S_asm(Q[0], Q[1],scalar_u, scalar_v);


        assembly{
